$().ready(function(){$(document.body).on("click",".taskDone",function(){var n=$(this);$.ajax({type:"POST",url:n.attr("data-url"),data:"taskId="+n.attr("data-id"),dataType:"json",success:function(t){t.IsSuccessfull==!0&&($(".task_"+n.attr("data-id")).remove(),$(".taskCount").html(parseInt($(".taskCount").html())-1))}})});$(document.body).on("click","#addTask",function(){var n=$(this),t="";t=$("#classId").val()!=""?n.attr("data-addTaskToClass"):n.attr("data-addTaskToUser");$.ajax({type:"POST",url:t,data:"userId="+$("#UserId").val()+"&message="+$("#Subject").val()+"&classId="+$("#classId").val(),dataType:"json",success:function(n){n.IsSuccessfull==!0&$("#classId").val()==""&&$("#tasksList").append("<div class='checkbox media m-r-20'>    <div class='media-body'>        <div>            <input type='checkbox'     class='EnableCheckbox' data-id='"+n.Result+"'>            <i class='input-helper'><\/i>            <div>"+$("#Subject").val()+"<\/div>        <\/div>    <\/div><\/div>")}})});$(document.body).on("click",".checkbox-taskDone",function(){var n=$(this);$.ajax({type:"POST",url:n.attr("data-url"),data:"taskId="+n.attr("data-id"),dataType:"json",success:function(t){t.IsSuccessfull==!0&&($(".task_"+n.attr("data-id")).remove(),$(".taskCount").html(parseInt($(".taskCount").html())-1))}})})});